import './App.css';
import {BrowserRouter, Route, Redirect } from 'react-router-dom';
import React,{useState,useEffect} from "react";
import AppHeader from "./components/Header/AppHeader";
import Home from './pages/Home/Home'
import Footer from "./components/footer/Footer";
import Catalog from './pages/Catalog/Catalog'

import coffee1 from "./img/coffee1.jpg";
import coffee2 from "./img/coffee2.jpg";
import coffee3 from "./img/coffee3.jpg";
import coffee4 from "./img/coffee4.jpg";
import coffee5 from "./img/coffee5.jpg";
import coffee6 from "./img/coffee6.jpg";

function App() {


    const [data, setData] = useState([
        {
            id:1,
            img: coffee1,
            name: 'Latte',
            price: 50,
            volumeInMl: 250
        },
        {
            id:2,

            img: coffee2,
            name: 'Americano',
            price: 50,
            volumeInMl: 250
        },
        {
            id:3,

            img: coffee3,
            name: 'Cacao',
            price: 50,
            volumeInMl: 250
        },
        {
            id:4,

            img: coffee4,
            name: 'Iced Caramel Macchiato',
            price: 50,
            volumeInMl: 250
        },
        {
            id:5,

            img: coffee5,
            name: 'Mocha',
            price: 50,
            volumeInMl: 250
        },
        {
            id:6,

            img: coffee6,
            name: 'Coffee',
            price: 50,
            volumeInMl: 250
        },

    ])
    const [searchItem, setSearchItem] = useState('')
    const [foundItems, setFoundItems] = useState([])
    const [redirect , setRedirect] = useState(true)


    const Filter = ()=> {
        const results = data.filter(item =>
            item.name.includes(searchItem)
        );

        setFoundItems(results);
    }


    console.log(foundItems)
    return (
        <BrowserRouter>
            <div className='App'>
                <AppHeader Filter={Filter} setSearchItem={setSearchItem} searchItem={searchItem} />
                { redirect ? (<Redirect push to="/"/>) : null }
                <Route exact path="/" render={() => <Home coffees = {data}/>}/>
                <Route path="/catalog" render={() => <Catalog coffees = {foundItems} /> }/>

                <Footer/>
            </div>
        </BrowserRouter>
    );
}

export default App;
