import './App.css';
import {BrowserRouter, Route, Redirect} from 'react-router-dom';
import React, {useState, useEffect} from "react";
import AppHeader from "./components/Header/AppHeader";
import Home from './pages/Home/Home'
import Footer from "./components/footer/Footer";
import Catalog from './pages/Catalog/Catalog'

import coffee1 from "./img/coffee1.jpg";
import coffee2 from "./img/coffee2.jpg";
import coffee3 from "./img/coffee3.jpg";
import coffee4 from "./img/coffee4.jpg";
import coffee5 from "./img/coffee5.jpg";
import coffee6 from "./img/coffee6.jpg";

function App() {

    const coffees = [
        {
            id: 1,
            img: coffee1,
            name: 'Latte',
            price: 44,
            volumeInMl: 300
        },
        {
            id: 2,

            img: coffee2,
            name: 'Americano',
            price: 86,
            volumeInMl: 200
        },
        {
            id: 3,

            img: coffee3,
            name: 'Cacao',
            price: 56,
            volumeInMl: 75
        },
        {
            id: 4,

            img: coffee4,
            name: 'Iced Caramel Macchiato',
            price: 43,
            volumeInMl: 100
        },
        {
            id: 5,

            img: coffee5,
            name: 'Mocha',
            price: 32,
            volumeInMl: 300
        },
        {
            id: 6,

            img: coffee6,
            name: 'Coffee',
            price: 55,
            volumeInMl: 120
        },
        {
            id: 7,

            img: coffee6,
            name: 'Coffee',
            price: 70,
            volumeInMl: 200
        },
    ]

    const [data, setData] = useState(coffees)
    const [searchItem, setSearchItem] = useState('')
    const [foundItems, setFoundItems] = useState([])
    const [filteredItems, setFilteredItems] = useState([])
    const [redirect, setRedirect] = useState(false)
    const [valueSort, setValueSort] = useState({kindSort: 'price', typeSort: 'desc'})

    const Search = () => {

        const results = data.filter(item =>
            item.name.includes(searchItem)
        );

        setFoundItems(results);
    }

    const Sort = () => {
        console.log(valueSort.typeSort)
        console.log(valueSort.kindSort)

        // if (foundItems.length === 0) {
        //     setFoundItems(data)
        // }

        if (valueSort.kindSort === 'price' && valueSort.typeSort === 'desc') {
            console.log("PRICE DESC")

            const results = data.sort(
                (first, second) => second.price - first.price);
            setFilteredItems(results);

        }
        if (valueSort.kindSort === 'price' && valueSort.typeSort === 'asc') {
            console.log("PRICE ASC")

            const results = data.sort(
                (first, second) => first.price - second.price);
            setFilteredItems(results);

        }
        if (valueSort.kindSort === 'volume' && valueSort.typeSort === 'desc') {
            console.log("VOLUME DESC")

            const results = data.sort(
                (first, second) => second.volumeInMl - first.volumeInMl);
            setFilteredItems(results);


        }
        if (valueSort.kindSort === 'volume' && valueSort.typeSort === 'asc') {
            console.log("VOLUME ASC")

            const results = data.sort(
                (first, second) => first.volumeInMl - second.volumeInMl);
            setFilteredItems(results);
        }


    }

    return (
        <BrowserRouter>
            <div className='App'>
                <AppHeader Search={Search} setRedirect={setRedirect} setSearchItem={setSearchItem}
                           searchItem={searchItem}/>
                {redirect ? (<Redirect push to="/catalog"/>) : null}
                <Route exact path="/" render={() => <Home coffees={data}/>}/>
                <Route path="/catalog"
                       render={() => <Catalog coffees={data} setValueSort={setValueSort} Sort={Sort}/>}/>

                <Footer/>
            </div>
        </BrowserRouter>
    );
}

export default App;
